// Helper functions
export function getSlot(slug: string): SlotData | undefined {
  return slots[slug];
}

export function getAllSlots(): SlotData[] {
  return Object.values(slots);
}

export function getSlotSlugs(): string[] {
  return Object.keys(slots);
}

export function getSlotsByProvider(provider: string): SlotData[] {
  return getAllSlots().filter(s =>
    s.hero.provider?.toLowerCase().includes(provider.toLowerCase())
  );
}

export function getSlotsByVolatility(volatility: string): SlotData[] {
  return getAllSlots().filter(s =>
    s.hero.volatility?.toLowerCase().includes(volatility.toLowerCase())
  );
}

export function searchSlots(query: string): SlotData[] {
  const q = query.toLowerCase();
  return getAllSlots().filter(s =>
    s.title.toLowerCase().includes(q) ||
    s.hero.title.toLowerCase().includes(q) ||
    s.hero.provider?.toLowerCase().includes(q)
  );
}

// Get slots by category using filter from categories config
export function getSlotsByCategory(categorySlug: string): SlotData[] {
  // Import dynamically to avoid circular dependency
  const { getSlotCategory } = require('./categories');
  const category = getSlotCategory(categorySlug);
  if (!category) return [];

  return getAllSlots().filter(slot => {
    // Parse RTP value from string
    const rtpMatch = slot.hero.rtp?.match(/(\d+\.?\d*)/);
    const rtpValue = rtpMatch ? parseFloat(rtpMatch[1]) : 0;

    return category.filter({
      slug: slot.slug,
      name: slot.hero.title,
      volatility: slot.hero.volatility,
      rtp: slot.hero.rtp,
      categoryTags: {
        ...slot.categoryTags,
        rtpValue,
        isHighRtp: rtpValue >= 96.5,
      },
    });
  });
}

// Re-export types
export type { SlotData, SlotRegistry } from './types';

// Re-export categories
export { SLOT_CATEGORIES, getSlotCategory, getAllCategorySlugs } from './categories';
